INSERT INTO `produk` VALUES (1,1,'INNOVA-ZENIX','RENTAL MOBIL INNOVA ZENIX','rental-innova-zenix.jpg','<p><strong>Sewa Innova Zenix</strong> di <a href=\"https://hafesrentcar.id\">PT Hafes Megah Lestari</a>. MPV hybrid modern dengan kenyamanan maksimal dan efisiensi bahan bakar terbaik.</p><p><strong>Fitur Unggulan:</strong></p><ul><li>Teknologi hybrid hemat BBM</li><li>Interior mewah dan lapang</li><li>Fitur keselamatan lengkap</li></ul><p><strong>Kapasitas:</strong> 7 penumpang</p><p><strong>Kelebihan:</strong> Cocok untuk perjalanan jauh dan keluarga besar.</p>','Sewa Innova Zenix hybrid nyaman dan hemat BBM.','rental-innova-zenix',1,7,'Y','2025-06-18 22:11:18','2025-06-18 22:11:18'),(2,1,'INNOVA-REBORN','RENTAL MOBIL INNOVA REBORN','rental-innova-reborn.jpg','<p><strong>Sewa Innova Reborn</strong> bersama <a href=\"https://hafesrentcar.id\">PT Hafes Megah Lestari</a>. Mobil legendaris dengan kenyamanan terbaik di kelasnya.</p><p><strong>Fitur:</strong></p><ul><li>AC double blower</li><li>Kabin luas dan empuk</li><li>Suspensi stabil</li></ul><p><strong>Kapasitas:</strong> 7 penumpang</p><p><strong>Kelebihan:</strong> Cocok untuk city tour dan keluarga.</p>','Innova Reborn andalan keluarga dan perusahaan.','rental-innova-reborn',1,7,'Y','2025-06-18 22:11:18','2025-06-18 22:11:18'),(3,1,'NEW-AVANZA','RENTAL MOBIL NEW AVANZA','rental-new-avanza.jpg','<p><strong>Grand Avanza</strong> tersedia di <a href=\"https://hafesrentcar.id\">PT Hafes Megah Lestari</a>. MPV ekonomis untuk segala kebutuhan.</p><p><strong>Fitur:</strong></p><ul><li>AC sejuk</li><li>Hemat bahan bakar</li><li>Kabin lapang</li></ul><p><strong>Kapasitas:</strong> 7 penumpang</p><p><strong>Kelebihan:</strong> Cocok untuk perjalanan keluarga hemat.</p>','Avanza pilihan tepat untuk efisiensi dan kenyamanan.','rental-new-avanza',1,7,'Y','2025-06-18 22:11:18','2025-06-18 22:11:18'),(4,1,'XPANDER','RENTAL MOBIL XPANDER','rental-xpander.jpg','<p><strong>X-Pander</strong> dari Mitsubishi hadir untuk pengalama